graphql.query:
mutation createRefreshTokenAuthorization ($input: CreateRefreshTokenAuthorizationInput!) {
    createRefreshTokenAuthorization (input: $input) {
        accessToken
        email
        expiresIn
        externalUid
        refreshToken
        tokenType
    }
}

mutation createRememberTokenAuthorization ($input: CreateRememberTokenAuthorizationInput!) {
    createRememberTokenAuthorization (input: $input) {
        accessToken
        email
        expiresIn
        externalUid
        refreshToken
        tokenType
    }
}

mutation createFacebookAuthorization ($traitsToOverride: PersonalizationTraitsInput, $storeIds: [Int], $input: CreateFacebookAuthorizationInput!) {
    createFacebookAuthorization (input: $input) {
        accessToken
        email
        expiresIn
        externalUid
        me {
            addresses {
                active
                address1
                address2
                addressId
                alternativePhone
                city
                country
                countryDetails {
                    id
                    iso
                    iso3
                    name
                }
                defaultFor
                firstName
                id
                insertedAt
                lastName
                phone
                postalCode
                state
                stateDetails {
                    id
                    iso
                    name
                }
                type
                userAddressId
                zipCode
            }
            commissionStatus {
                amountUntilNextRate
                currentWindowSalesCents
                forMonth
                lifetimeSalesCents
                nextPaymentStatus {
                    amountCents
                    issuedOn
                }
                nextRate
                paymentDate
                pendingCommissions
                pendingCommissionsCents
                previousPaymentStatus {
                    amountCents
                    issuedOn
                }
                rate
                rateExpirationDate
                sustainabilityReport {
                    carbonEmissionPreventedInKilograms
                    waterWastePreventedInLiters
                }
                tier
                totalSalesCents
            }
            createdAt
            currency {
                exponent
                id
                iso
                name
                prefix
            }
            email
            emailPreference {
                categoryNames
                consignmentOffers
                emailSubscription
                frequency
                newsletterConsign
                unsubscribeUntil
            }
            firstName
            giftCardCreditsTotal {
                formatted
                usdCents
            }
            id
            lastName
            lastSignInAt
            location {
                customName
                latitude
                longitude
            }
            membership {
                expiresAt
                price
                startsAt
                type
            }
            otherCreditsTotal {
                formatted
                usdCents
            }
            paymentMethods
            paymentPreference {
                address {
                    address1
                    address2
                    alternativePhone
                    city
                    country {
                        id
                        iso
                        iso3
                        name
                    }
                    firstName
                    lastName
                    phone
                    state {
                        abbreviation
                        name
                    }
                    zip
                }
                label
                type
            }
            personalizationTraits (traitsToOverride: $traitsToOverride)
            phone
            referralCreditsTotal {
                formatted
                usdCents
            }
            sizePreferences {
                buckets {
                    clothingSize
                    shoeSize
                }
                name
            }
            slug
            smsSubscription {
                mobileNumber
                optIn
            }
            storeCredit
            storeCreditsHistory {
                createdAt
                description
                expiresAt
                id
                remainingAmount
            }
            storeCreditsTotal {
                formatted
                usdCents
            }
            tips {
                newFeed
                saveSearch
                saveSearchMyTrr
                sizePreferences
            }
            totalCredits {
                formatted
                usdCents
            }
            traits {
                existingConsignor
                existingPurchaser
                gender
            }
            viewedProducts {
                artist {
                    description
                    id
                    name
                }
                condition
                variantId
                images {
                    alt
                    giftCardImageType
                    height
                    size
                    url
                    width
                }
                marketingContent {
                    icon {
                        alt
                        giftCardImageType
                        height
                        size
                        url
                        width
                    }
                    isAffirm
                    mainText {
                        level
                        text
                        type
                    }
                }
                storeContactInfo {
                    address {
                        level
                        text
                        type
                    }
                    availability {
                        level
                        text
                        type
                    }
                    fullAddress
                    hours {
                        level
                        text
                        type
                    }
                    name {
                        level
                        text
                        type
                    }
                    phone
                    storeId
                    url
                }
                merchandisingContent
                waitlisted
                newSizeGuide
                measurementGuide {
                    image
                    measurements {
                        description
                        label
                    }
                }
                valuationReportUrl
                disclaimers
                returnPolicy {
                    description
                    id
                }
                quantity
                warehouseAvailability {
                    bannerDesktopImage {
                        alt
                        giftCardImageType
                        height
                        size
                        url
                        width
                    }
                    bannerMobileImage {
                        alt
                        giftCardImageType
                        height
                        size
                        url
                        width
                    }
                    checkoutShortText {
                        level
                        text
                        type
                    }
                    checkoutText {
                        level
                        text
                        type
                    }
                    emailText {
                        level
                        text
                        type
                    }
                    globalColor
                    globalFontColor
                    globalFontFamily
                    globalFontSize
                    globalShortText {
                        level
                        text
                        type
                    }
                    globalText {
                        level
                        text
                        type
                    }
                }
                designer {
                    id
                    name
                    restriction
                    slug
                    type
                }
                curbsidePickupStoreInfo (storeIds: $storeIds) {
                    messages {
                        multipleStoresItems
                        notEligibleItem
                    }
                    plpBadge
                }
                sku
                url
                price {
                    discount
                    final {
                        formatted
                        usdCents
                    }
                    msrp {
                        formatted
                        usdCents
                    }
                    original {
                        formatted
                        usdCents
                    }
                }
                taxons {
                    id
                    name
                    parentId
                    permalink
                    seoName
                    seoPermalink
                }
                authenticationContent {
                    mainText {
                        level
                        text
                        type
                    }
                }
                representative {
                    id
                    url
                    variantId
                }
                sustainabilitySummary {
                    kilogramsOfCarbon
                    kilometersTraveled
                    litersOfWater
                }
                attributes {
                    label
                    type
                    values
                }
                category {
                    id
                    label
                    name
                }
                slug
                id
                brand {
                    name
                    slug
                }
                availability
                firstLookProduct
                promotion {
                    code
                    label
                }
                sizeGuide {
                    columnHeaders
                    rows {
                        header
                        values
                    }
                }
                description
                editorsPick
                store
                name
                glossaryTerms {
                    categoryId
                    description
                    image {
                        alt
                        giftCardImageType
                        height
                        size
                        url
                        width
                    }
                    imageLocation
                    name
                    pluralizedName
                    readMore
                    subCategory
                }
                brandUnion
                components {
                    attributes {
                        label
                        type
                        values
                    }
                }
                obsessed
                warehouseId
            }
            withdrawalMethod
        }
        refreshToken
        tokenType
    }
}

